import ProductsList from "~/components/molecules/ProductsList";
import CategoriesList from "~/components/molecules/CategoriesList";
export default {
  components: {
    ProductsList,
    CategoriesList,
  },
  data() {
    return {
      jsonld: {
        "@context": "https://schema.org",
        "@type": "BreadcrumbList",
        itemListElement: [
          {
            "@type": "ListItem",
            position: 1,
            item: {
              "@id": process.env.APP_URL,
              name: "Главная",
            },
          },
          {
            "@type": "ListItem",
            position: 2,
            item: {
              "@id": process.env.APP_URL + "/catalog/",
              name: "Каталог",
            },
          },
          {
            "@type": "ListItem",
            position: 3,
            item: {
              "@id":
                process.env.APP_URL +
                "/" +
                this.$page.strapi.categories[0].slug +
                "/",
              name: this.$page.strapi.categories[0].name,
            },
          },
        ],
      },
    };
  },
  metaInfo() {
    return {
      title: this.$page.strapi.categories[0].Seo.metaTitle,
      script: [
        { innerHTML: JSON.stringify(this.jsonld), type: "application/ld+json" },
      ],
      meta: [
        {
          name: "description",
          content: this.$page.strapi.categories[0].Seo.metaDescription,
        },
        {
          property: "og:title",
          content: this.$page.strapi.categories[0].Seo.metaTitle,
        },
        {
          property: "og:description",
          content: this.$page.strapi.categories[0].Seo.metaDescription,
        },
        {
          property: "og:image",
          content: this.$page.strapi.categories[0].Seo.shareImage.image.url,
        },
      ],
    };
  },
};
