import { isMobile, fullBrowserVersion } from "mobile-device-detect";
import VueMarkdown from "vue-markdown";

var moment = require("moment");

export default {
  data() {
    return {
      moment: moment,
      isMobile: false,
      jsonld: {
        "@context": "https://schema.org",
        "@type": "BreadcrumbList",
        itemListElement: [
          {
            "@type": "ListItem",
            position: 1,
            item: {
              "@id": process.env.APP_URL,
              name: "Главная",
            },
          },
          {
            "@type": "ListItem",
            position: 2,
            item: {
              "@id": process.env.APP_URL + "/catalog/",
              name: "Каталог",
            },
          },
          {
            "@type": "ListItem",
            position: 3,
            item: {
              "@id":
                process.env.APP_URL +
                "/" +
                this.$page.strapi.products[0].category.slug +
                "/",
              name: this.$page.strapi.products[0].category.name,
            },
          },
          {
            "@type": "ListItem",
            position: 4,
            item: {
              "@id":
                process.env.APP_URL +
                "/" +
                this.$page.strapi.products[0].slug +
                "/",
              name: this.$page.strapi.products[0].title,
            },
          },
        ],
      },
    };
  },
  components: {
    VueMarkdown,
  },
  metaInfo() {
    return {
      title: this.$page.strapi.products[0].Seo.metaTitle,
      script: [
        { innerHTML: JSON.stringify(this.jsonld), type: "application/ld+json" },
      ],
      meta: [
        {
          name: "description",
          content: this.$page.strapi.products[0].Seo.metaDescription,
        },
        {
          property: "og:title",
          content: this.$page.strapi.products[0].Seo.metaTitle,
        },
        {
          property: "og:description",
          content: this.$page.strapi.products[0].Seo.metaDescription,
        },
        {
          property: "og:image",
          content: this.$page.strapi.products[0].Seo.shareImage.image.url,
        },
      ],
    };
  },
};
