import VueMarkdown from "vue-markdown"

// var moment = require("moment");

export default {
  data() {
    return {
      // moment: moment,
      jsonld: {
        "@context": "https://schema.org",
        "@type": "BreadcrumbList",
        itemListElement: [
          {
            "@type": "ListItem",
            position: 1,
            item: {
              "@id": process.env.APP_URL,
              name: "Главная",
            },
          },
          {
            "@type": "ListItem",
            position: 2,
            item: {
              "@id": process.env.APP_URL + "/news/",
              name: "Новости",
            },
          },
          {
            "@type": "ListItem",
            position: 3,
            item: {
              "@id":
                process.env.APP_URL +
                "/" +
                this.$page.strapi.posts[0].slug +
                "/",
              name: this.$page.strapi.posts[0].title,
            },
          },
        ],
      },
    }
  },
  components: {
    VueMarkdown,
  },
  metaInfo() {
    return {
      title: this.$page.strapi.posts[0].Seo.metaTitle,
      script: [
        { innerHTML: JSON.stringify(this.jsonld), type: "application/ld+json" },
      ],
      meta: [
        {
          name: "description",
          content: this.$page.strapi.posts[0].Seo.metaDescription,
        },
        {
          property: "og:title",
          content: this.$page.strapi.posts[0].Seo.metaTitle,
        },
        {
          property: "og:description",
          content: this.$page.strapi.posts[0].Seo.metaDescription,
        },
        {
          property: "og:url",
          content:
            process.env.APP_URL + "/" + this.$page.strapi.posts[0].slug + "/",
        },
        {
          property: "og:image",
          content: this.$page.strapi.posts[0].Seo.shareImage.image.url,
        },
        {
          property: "og:type",
          content: "article",
        },
        // {
        //   property: "og:site_name",
        //   content: process.env.APP_NAME,
        // },
      ],
    }
  },
}
